\copy (
  select r.repo_group as project, a.login, a.name, a.country_name, aa.dt_from as affiliated_from, aa.dt_to as affiliatied_to, min(date(e.created_at)) as first_contribution, max(date(e.created_at)) as last_contribution, count(distinct e.id) as contributions from gha_actors_affiliations aa, gha_actors a, gha_events e, gha_repos r where r.repo_group is not null and e.dup_repo_name = r.name and a.login = e.dup_actor_login and aa.actor_id = a.id and e.created_at >= aa.dt_from and e.created_at < aa.dt_to and lower(aa.company_name) = '{{company}}' and e.type in ('IssuesEvent', 'PullRequestEvent', 'PushEvent', 'CommitCommentEvent', 'IssueCommentEvent', 'PullRequestReviewCommentEvent') group by r.repo_group, a.login, a.name, a.country_name, aa.dt_from, aa.dt_to order by project asc, contributions desc
) to '/tmp/split_{{company}}.csv' with csv delimiter ',' header;
